import { useEffect, useState } from "react";



export default function Listings() {
  const [jobs, setJobs] = useState([]);
  const [allJobs, setAllJobs] = useState([]);

  async function fetchData() {
    try {
      const resp = await fetch("http://localhost:5000/jobs", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await resp.json();
      setJobs(data);
      setAllJobs(data);
    } catch (err) {
      console.log(err);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);



  return (
    <main className="grid gap-10 bg-[#effafa] px-6 py-14">
      {jobs.map((job) => (
        <div
          key={job.id}
          className="relative grid gap-2 bg-white px-6 pt-8 pb-6 rounded-md shadow-md"
        >
          {job.featured ? (
            <div className="absolute left-0 top-0 h-full w-1.5 bg-[#5ba4a4] rounded-l-md"></div>
          ) : (
            ""
          )}
          <img
            className="absolute top-[-1.5rem] left-6 w-12"
            src={job.logo}
            alt={`${job.company} logo`}
          />
          <div className="flex gap-6 items-center">
            <h1 className="text-[#5ba4a4] font-bold tracking-tight">
              {job.company}
            </h1>
            <div className="flex gap-1.5">
              {job.new ? (
                <span className="bg-[#5ba4a4] text-white uppercase font-bold text-sm px-2 py-0.5 rounded-full">
                  New!
                </span>
              ) : (
                ""
              )}
              {job.featured ? (
                <span className="bg-[#2c3a3a] text-white uppercase font-bold text-sm px-2 py-0.5 rounded-full">
                  Featured
                </span>
              ) : (
                ""
              )}
            </div>
          </div>
          <div className="border-b">
            <h2 className="font-bold tracking-tight">{job.position}</h2>
            <div className="flex items-center gap-2.5 mt-2 mb-4">
              <span className="text-[#7b8e8e]">{job.postedAt}</span>
              <div className="w-1 h-1 rounded-full bg-[#7b8e8e]"></div>
              <span className="text-[#7b8e8e]">{job.contract}</span>
              <div className="w-1 h-1 rounded-full bg-[#7b8e8e]"></div>
              <span className="text-[#7b8e8e] ml-[-0.25rem]">
                {job.location}
              </span>
            </div>
          </div>
          <div className="flex flex-wrap gap-4 mt-2 font-bold">
            <span
              className="bg-[#eef6f6] text-[#5ba4a4] px-[0.5625rem] py-1 rounded-md hover:cursor-pointer"

            >
              {job.role}
            </span>
            <span
              className="bg-[#eef6f6] text-[#5ba4a4] px-[0.5625rem] py-1 rounded-md hover:cursor-pointer"
              onClick={filterListing}
            >
              {job.level}
            </span>
            {job.languages.map((language, index) => (
              <span
                key={index}
                className="bg-[#eef6f6] text-[#5ba4a4] px-[0.5625rem] py-1 rounded-md hover:cursor-pointer"

              >
                {language}
              </span>
            ))}
            {job.tools
              ? job.tools.map((tool, index) => (
                  <span
                    key={index}
                    className="bg-[#eef6f6] text-[#5ba4a4] px-[0.5625rem] py-1 rounded-md hover:cursor-pointer"

                  >
                    {tool}
                  </span>
                ))
              : ""}
          </div>
        </div>
      ))}
    </main>
  );
}
