import closeIcon from "../images/icon-remove.svg";

export default function FilterBox({ tags, clearAll }) {
  return (
    <div className="flex flex-wrap items-center gap-2 bg-white p-4">
        {tags.map((tag) => (
          <div className="flex items-center">
            <span className="bg-[#eef6f6] text-[#5ba4a4] font-bold px-2 py-1 rounded-l-lg">
              {tag}
            </span>
            <img
              className="bg-[#5ba4a4] p-[0.5625rem] rounded-r-lg hover:cursor-pointer hover:bg-[#2c3a3a]"
              src={closeIcon}
              alt="CloseIcon"
            />
          </div>
        ))}
      <span
        onClick={clearAll}
        className="font-bold text-[#7b8e8e] hover:text-[#5ba4a4] hover:text-decoration-line:underline hover:cursor-pointer hover:underline"
      >
        Clear
      </span>
    </div>
  );
}
