import { useEffect, useState } from "react";

export default function Listing() {
  const [jobs, setJobs] = useState([]);
  const [allJobs, setAllJobs] = useState([]);

  async function fetchData() {
    try {
      const resp = await fetch("https://api.npoint.io/8162247b84ab695d6a5e", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await resp.json();
      setJobs(data);
      setAllJobs(data);
    } catch (err) {
      console.log(err);
    }
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <main className="grid pt-14 px-6 bg-[#effafa]">
      {jobs.map((job) => (
        <div key={job.id} className="relative bg-white rounded-lg py-8 px-6">
          {job.featured && <div className="absolute h-full top-0 left-0 w-1 bg-[#5ba4a4] rounded-l-lg"></div>}
          <img className="absolute top-[-1.5rem] left-6 w-12" src={job.logo} alt={`${job.company} logo`} />
          <div className="flex gap-5 items-center">
            <h1 className="text-[#5ba4a4] font-bold tracking-tight">{job.company}</h1>
            <div className="flex gap-1.5">
              {job.new && <span className="uppercase bg-[#5ba4a4] text-white text-sm py-0.5 px-2.5 rounded-full">new!</span>}
              {job.featured && <span className="uppercase bg-[#2c3a3a] text-white text-sm py-0.5 px-2.5 rounded-full">featured</span>}
            </div>
          </div>
          <p className="text-[#2c3a3a]">{job.position}</p>
          <div>
            <span>{job.postedAt}</span>
            <span>{job.contract}</span>
            <span>{job.location}</span>
          </div>
          <span className="bg-[#eef6f6] text-[#5ba4a4] font-bold">
            {job.role}
          </span>
          <span className="bg-[#eef6f6] text-[#5ba4a4] font-bold">
            {job.level}
          </span>
          {job.languages.map((language) => (
            <span className="bg-[#eef6f6] text-[#5ba4a4] font-bold">
              {language}
            </span>
          ))}
          {job.tools &&
            job.tools.map((tool) => (
              <span className="bg-[#eef6f6] text-[#5ba4a4] font-bold">
                {tool}
              </span>
            ))}
        </div>
      ))}
    </main>
  );
}
